import  {
    GET_ALL_DOG,
    GET_NAME_DOG,
    GET_TEMPERAMENT,
    DETAIL_DOG,
    POST_DOG,
    ORDER_BY_RAZA,
    EXISTENT_DOG
}  from "../Actions/Action";

const inicialState = {
    allDogs: [],
    temperament: [],
    detailDog: [],
    allRaza: []
}

// REDUCER

const rootReducer = (state = inicialState, action) => {
    switch(action.type){
        case GET_ALL_DOG:
            return {
                ...state,
                allDogs: action.payload,
                allRaza: action.payload
            }
        case GET_NAME_DOG:
            return {
                ...state,
                allDogs: action.payload
            }
        case GET_TEMPERAMENT:
            return {
                ...state, 
                temperament: action.payload
            }
        case DETAIL_DOG:
            return {
                ...state,
                detailDog: action.payload
            }
        case POST_DOG:
            return {
                ...state
            }
        // FILTROS:
        case ORDER_BY_RAZA:
            let newStateRaza = state.allRaza;
            let sortedArr = action.payload === 'desc' ? 
                newStateRaza.sort(function(a, b){
                    if(a.name > b.name){
                        return 1;
                    }
                    if(b.name > a.name){
                        return -1;
                    }
                    return 0;
                })
                :
                newStateRaza.sort(function (a, b){
                    if(a.name > b.name){
                        return -1;
                    }
                    if(b.name > a.name){
                        return 1;
                    }
                    return 0;
                })
            return {
                ...state,
                allDogs: sortedArr.map(e => e)
            }
        case EXISTENT_DOG:
            let newStateExistent = state.allRaza;
            const filterExistent = action.payload === 'created' ? 
                newStateExistent.filter(el => el.createdInDb === true)
                :
                newStateExistent.filter(el => !el.createdInDb);
            return{
                ...state,
                allDogs: action.payload === 'all' ? newStateExistent : filterExistent
            }
        default: 
            return state;
    }
};

export default rootReducer;
