import  {
    GET_ALL_DOG,
    GET_NAME_DOG,
    GET_TEMPERAMENT,
    // POST_DOG
}  from "../Actions/Action";

const inicialState = {
    allDogs: [],
    temperament: []
}

// REDUCER

const rootReducer = (state = inicialState, action) => {
    switch(action.type){
        case GET_ALL_DOG:
            return {
                ...state,
                allDogs: action.payload
            }
        case GET_NAME_DOG:
            return {
                ...state,
                allDogs: action.payload
            }
        case GET_TEMPERAMENT:
            return {
                ...state, 
                temperament: action.payload
            }
        case DETAIL_DOG:
            return {
                ...state,
                detailDog: action.payload
            }
        // case POST_DOG:
        //     return {
        //         ...state,
        //         allDogs: action.payload
        //     }
        default: 
            return state;
    }
};

export default rootReducer;
