import React, { useEffect } from "react";
import { 
    useState, 
    // useEffect 
} from "react";
import { Link } from  'react-router-dom';
import { 
    // useDispatch, 
    useSelector 
} from "react-redux";
// import { postDog} from '../Redux/Actions/Action';
import './Creation.css';






function Creacion(){
    // const dispatch = useDispatch();

    const AllTemperament = useSelector((state) => state.temperament)
    // let AllTemperament = [
    //     {
    //       "id": 1,
    //       "temperament": "Stubborn"
    //     },
    //     {
    //       "id": 2,
    //       "temperament": " Curious"
    //     },
    //     {
    //       "id": 3,
    //       "temperament": " Playful"
    //     },
    //     {
    //       "id": 4,
    //       "temperament": " Fun-loving"
    //     },
    //     {
    //       "id": 5,
    //       "temperament": " Adventurous"
    //     },
    //     {
    //       "id": 6,
    //       "temperament": " Clownish"
    //     },
    //     {
    //       "id": 7,
    //       "temperament": " Active"
    //     },
    //     {
    //       "id": 8,
    //       "temperament": "Aloof"
    //     },
    //     {
    //       "id": 9,
    //       "temperament": " Dignified"
    //     },
    //     {
    //       "id": 10,
    //       "temperament": " Happy"
    //     },
    //     {
    //       "id": 11,
    //       "temperament": " Independent"
    //     },
    //     {
    //       "id": 12,
    //       "temperament": " Hardworking"
    //     },
    //     {
    //       "id": 13,
    //       "temperament": "Wild"
    //     },
    //     {
    //       "id": 14,
    //       "temperament": " Dutiful"
    //     },
    //     {
    //       "id": 15,
    //       "temperament": "Outgoing"
    //     },
    //     {
    //       "id": 16,
    //       "temperament": " Friendly"
    //     },
    //     {
    //       "id": 17,
    //       "temperament": " Alert"
    //     },
    //     {
    //       "id": 18,
    //       "temperament": " Confident"
    //     },
    //     {
    //       "id": 19,
    //       "temperament": " Intelligent"
    //     },
    //     {
    //       "id": 20,
    //       "temperament": " Courageous"
    //     },
    //     {
    //       "id": 21,
    //       "temperament": "Loyal"
    //     },
    //     {
    //       "id": 22,
    //       "temperament": " Brave"
    //     },
    //     {
    //       "id": 23,
    //       "temperament": "Docile"
    //     },
    //     {
    //       "id": 24,
    //       "temperament": " Responsive"
    //     },
    //     {
    //       "id": 25,
    //       "temperament": " Receptive"
    //     }
    // ]
    console.log('todos los temperamentos ->', AllTemperament);
    
    
    const [input, setInput ] = useState({
        name:"",
        image: "",
        raza: "",
        temperament: "",
        height: 0,
        weight: 0,
        year: 0
    })
    
    // inicializacion de rangos input "range":
    const [rangoHp, setRangoHp] = useState(0);
    const [rangoAttack, setRangoAttack] = useState(0);
    const [rangoDefense, setRangoDefense] = useState(0);


    useEffect(() => {
        dis
    })




    function handleChange(e){
        console.log('valor input-->', input)
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    function handleSubmit(e){
        e.preventDefault();
        // setInput({...input.name=input.name.toLowerCase()});
        alert('personaje creado');
        setInput({
            name:"",
            image: "",
            raza: "",
            temperament: "",
            height: 0,
            weight: 0,
            year: 0
        })
        // dispatch(postDog(input));
        //useHistory.push('/home');
    }

    // let validateName = /^[a-zA-Z\s]+$/;
    // let validateUrl = /(http(s?):)([/|.|\w|\s|-])*.(?:jpg|gif|png)/;

    return (
        <>
        <Link to='./home' ><button className="botonSubmit">volver</button></Link>
        <h1 className="tituloCreacion">Crea tu mascota</h1>
        <form 
            onSubmit={(e)=>handleSubmit(e)}
        >
            <div>
                <div>
                    <label>Nombre:</label>
                    <input 
                        className="field"
                        type='text'
                        value={input.name}
                        name='name'
                        onChange={handleChange}
                    />
                </div>
            
                <div>
                    <label>Imagen:</label>
                    <input 
                        className="field"
                        type='text'
                        value={input.image}
                        name='image'
                        onChange={handleChange}
                    />
                </div>
                {/* // [ ] Botón/Opción para crear una nueva raza de perro -> STRING? */}
                <div>
                    <label>Raza de perro:</label>
                    <input
                        className="field"
                        type='text'
                        value={input.raza}
                        name='raza'
                        onInput={(e) => setRangoDefense(e.target.value)}
                        onChange={handleChange}
                    />
                </div>

                <div>
                    <label>Temperamento:</label>
                    <select
                        className="field"
                        type='text'
                        value={input.temperament}
                        name='temperament'
                        onInput={(e) => setRangoDefense(e.target.value)}
                        onChange={handleChange}
                    >
                        {
                            AllTemperament?.map((el, i) =>{
                                return (
                                    <option
                                        key={i}
                                        value={el}
                                        // onClick={ (e) => handleOption(e) }
                                    >
                                        {el.temperament}
                                    </option>
                                )
                            })
                        }    
                    </select>
                    {/*

                    <select>
                    {
                    temps?.map((ele,i)=>{
                        return(
                        <option 
                        key={i} 
                        value={ ele }
                        onClick={ (e)=>handleOption(e) } 
                        >{ele}</option>
                        )
                    })
                    }
                    </select>
                    
                    */}
                </div>
                
            </div>

            <div>
                <div>
                    <label>Altura: {rangoHp}</label>
                    <input 
                        className="field"
                        type='range'
                        value={input.height}
                        name='height'
                        onInput={(e) => setRangoHp(e.target.value)}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Peso: {rangoAttack}</label>
                    <input 
                        className="field"
                        type='range'
                        value={input.weight}
                        name='weight'
                        onInput={(e) => setRangoAttack(e.target.value)}
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label>Año: {rangoDefense}</label>
                    <input
                        className="field"
                        type='range'
                        value={input.year}
                        name='year'
                        onInput={(e) => setRangoDefense(e.target.value)}
                        onChange={handleChange}
                    />
                </div>
                
                
                
                <button className="botonSubmit" type="submit">Crear Personaje</button>
            </div>

        </form>
        </>
    )
}

export default Creacion;





// Ruta de creación de raza de perro: debe contener

    // [ ] Un formulario controlado con JavaScript con los siguientes campos:
    // Nombre -> ESTRING
    // Altura (Diferenciar entre altura mínima y máxima) -> NUMBER
    // Peso (Diferenciar entre peso mínimo y máximo) -> NUMBER
    // Años de vida -> NUMBER

    // [ ] Posibilidad de seleccionar/agregar uno o más temperamentos -> SELECT CON TEMPERAMENTOS

    // [ ] Botón/Opción para crear una nueva raza de perro -> STRING?
    // validaciones =>
    // Es requisito que el formulario de creación esté validado con JavaScript y no sólo con validaciones HTML. Pueden agregar las validaciones que consideren. 
    
    // Por ejemplo: Que el nombre de la raza no pueda contener números o símbolos, que el peso/altura mínimo no pueda ser mayor al máximo y viceversa, etc.
