import React, { useEffect, useState } from "react";
import SearchBar from "./SearchBar";
import {useDispatch, useSelector} from 'react-redux';
import { getAllDogs } from '../Redux/Actions/Action';
import { Link } from "react-router-dom";
import './Home.css'

import Card from "./Card";
import Paginado from "./Paginado";

function Home(){

    const dispatch = useDispatch();
    // geteo  el estado global de Redux:
    const AllDogs = useSelector((state) => state.allDogs);
    // console.log('useSelector ->', AllDogs);


    // PAGINADO:
    const [ currentPage, setCurrentPage ] = useState(1);
    const [ dogPerPage, setDogPerPage ] = useState(8);
    const indexOfLastDog = currentPage * dogPerPage;
    const indexOfFirstDog = indexOfLastDog - dogPerPage;
    const currentDog = AllDogs?.slice(indexOfFirstDog, indexOfLastDog);

    const paginado = (pageNumber) => {
        setCurrentPage(pageNumber);
    }
    //  ------------------>


    useEffect(()=>{
        dispatch(getAllDogs())
    }, [dispatch]);

    return (
        <>
        <div>
            <SearchBar />
        </div>
        <div className="bodyContainer">
            <div className="menuContainer">
                <ul className="ulMenu">
                    <li><select> <option value={1}> optionSelect </option> </select></li>
                    <li><select> <option value={2}> optionSelect </option> </select></li>
                    <li><select  > <option value={3}> optionSelect </option> </select></li>
                </ul>
            </div>
            <div className="cardContainer">
                {
                    currentDog.map((el) => {
                        return (
                            <div key={el.id}>
                                <Link to={'/detail/' + el.id}>
                                    <Card  
                                        id={el.id} 
                                        name={el.name} 
                                        temperament={el.temperament} 
                                        img={el.image} 
                                    />
                                </Link>
                            </div>
                        )
                    })
                }
            </div>
        </div>

        <footer>
            <div>
                <Paginado 
                    dogPerPage={dogPerPage}
                    allDogs={AllDogs.length}
                    paginado={paginado}
                />
            </div>
        </footer>
        </>
    )
}

export default Home;
